import React, { useState, useEffect } from 'react';
import { Link } from "react-router";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import { contains } from 'jquery';
import img from "../../Images/axis.png";

export default function Site1 () {
  const [ready, setReady] = useState(false);
  const [theArray, setTheArray] = useState();
  
  useEffect(() => {
    // get all the devices from db when site render
    getDevices();
  }, []);

  async function getDevices() {
    let uri = 'http://localhost:3000/devices';
    let thisSiteId = 1;
    const res = await fetch(uri);
    const posts = await res.json();
    let array = []
    console.log(posts)
    posts.map(device =>  {
    if (device.site_id == thisSiteId) {
       array.push(device)
    }     
    })
    setTheArray(array);
    setReady(true);
  }
  return (
    ready && (

<Container>
  <Row>
  {theArray.map(device => (
  <Col>
    <img src="./Images/axisCam.jpg"></img>
    <h2 id = "title">
    {device.title}
    <p id = "description">
    {device.description}
    </p>
    <p id = "model">
    {device.model}
    </p>
    <p id = "model">
    {device.version}
    </p>
  </h2>
  </Col>



)) }

  </Row>
  </Container>       
          

  ));
}
