import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import img1 from "../Images/axis-M2025.jpg";
import img2 from "../Images/axis-p1428.jpg";
import img3 from "../Images/axis-Q6000.jpg";

export default function Site1() {
  const [ready, setReady] = useState(false);
  const [theArray, setTheArray] = useState();
  const { id } = useParams();

  useEffect(() => {
    getDevices();
  }, []);

  async function getDevices() {
    let uri = "http://localhost:3000/devices";
    let thisSiteId = id;
    const res = await fetch(uri);
    const posts = await res.json();
    let array = [];
    posts.map((device) => {
      if (device.site_id == thisSiteId) {
        array.push(device);
      }
    });
    setTheArray(array);
    setReady(true);
  }
  return (
    ready && (
      <Container fluid>
        {theArray.map((device, index) => (
          <Row key={device.id}>
            <Col>
              <h2 id="title">{device.title}</h2>
              <p id="description">{device.description}</p>
              <p id="model">
                <b>Model: </b> {device.model}
              </p>
              <p id="version">
                <b>Version: </b>
                {device.version}
              </p>
              <p id="enabled">
                <b>Enabled: </b>
                {device.enabled ? "enabled" : "not enabled"}
              </p>
              <p id="connected">
                <b>Connected: </b>
                {device.connected ? "connected" : "not connected"}
              </p>
              <p id="timezone">
                <b>Timezone: </b>
                {device.timezone}
              </p>
              <div id="storages">
                <b>Storages: </b>
                {device.storages.map((storage) =>
                  storage.id ? (
                    <p >
                      {storage.id}
                      <br></br>
                      <b>State: </b>
                      {storage.state}
                    </p>
                  ) : null
                )}
              </div>
            </Col>
          </Row>
        ))}
      </Container>
    )
  );
}
