import Col from 'react-bootstrap/Col'

export default function Site1 () {
  const [ready, setReady] = useState(false);
  const [theArray, setTheArray] = useState([]);
  
  useEffect(() => {
    // get all the devices from db when site render
    getDevices();
  }, []);

  async function getDevices() {
    let uri = 'http://localhost:3000/devices';
    let thisSiteId = 1;
    const res = await fetch(uri);
    const posts = await res.json();
    let array = []
    console.log(posts)
    posts.map(device =>  {
      console.log(device)
    if (device.site_id == thisSiteId) {
       array.push(device)
    }     
    })
    setTheArray(oldarray => [...oldarray, array]);
    setReady(true);
  }


  return (
    ready && (
    <Container>
  <Row>
    <Col>
   {

theArray.map(device => (
  console.log(device)
 
)) }
         
          
       </Col>
    <Col>2 of 3</Col>
    <Col>3 of 3</Col>
  </Row>
</Container>
  ));
}
