import React, { useState, useEffect } from 'react';
import { Link } from "react-router";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default function Site1 () {
  useEffect(() => {
    // get all the devices from db when site render
    getDevices();
  });
  const thisSitesDevices = []
  const [ready, setReady] = useState(false);

  const getDevices = async () => {
    let uri = 'http://localhost:3000/devices';
    let thisSiteId = 1;
    const res = await fetch(uri);
    const posts = await res.json();
    posts.map(device =>  {
    if (device.site_id == thisSiteId) {
        thisSitesDevices.push(device)
        var result = thisSitesDevices.map(x => ({ value: x.id, text: x.description }));
        console.log(result)
        setReady(true);
        console.log(ready)
    }
    })}

  return (
    ready && (
    <Container>
  <Row>
    <Col>
           {thisSitesDevices.map(info => (<div key={info}> {info} </div>))} 
          
       </Col>
    <Col>2 of 3</Col>
    <Col>3 of 3</Col>
  </Row>
</Container>
  ));
}
