import { BrowserRouter, Route, Switch } from "react-router-dom";
import NavBarLogin from "./Components/Layout/NavbarLogin";
import NavBarLogout from "./Components/Layout/NavbarLogout";
import "./styles.css";
import Site1 from "./Sites/Site1";
import Site2 from "./Sites/Site2";
import Site3 from "./Sites/Site3";
import React, { useState, useEffect } from "react";
import { AppContext } from "./Libs/contextLib";
import Login from "./Components/Auth/Login";

export default function App() {
  const [isAuthenticated, userHasAuthenticated] = useState(false);

  const logIn = async () => {
    let uri = 'http://localhost:3000/users';
    const res = await fetch(uri);
    const posts = await res.json();

    posts.map(name =>  {
      if(name.username === username && name.password === password) {
        userHasAuthenticated(true);
      }
    })}

  return (
    <AppContext.Provider value={{ isAuthenticated, userHasAuthenticated }}>
        {isAuthenticated ? (
        <BrowserRouter>
        <div className="app">
          <NavBarLogout />
          <Switch>
            <Route exact path="/" component={Site1} />
            <Route exact path="/login" component={Login} />

            <Route path="/2" component={Site2} />

            <Route path="/3" component={Site3} />
          </Switch>
        </div>
      </BrowserRouter>

      ) : (
        <>
        <Login />
         </>

  )
      }
  </AppContext.Provider>

  )}
