import React, { useState, useEffect } from 'react';
import { Link, useParams } from "react-router";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import img1 from "../Images/axis-M2025.jpg";
import img2 from "../Images/axis-p1428.jpg";
import img3 from "../Images/axis-Q6000.jpg";

export default function Site1 () {
  const [ready, setReady] = useState(false);
  const [theArray, setTheArray] = useState();
  const { id } = useParams();
  
  useEffect(() => {
    getDevices();
  }, []);

  async function getDevices() {
    let uri = 'http://localhost:3000/devices';
    let thisSiteId = id;
    getUsers();
    const res = await fetch(uri);
    const posts = await res.json();
    let array = []
    posts.map(device =>  {
    if (device.site_id == thisSiteId) {
       array.push(device)
    }     
    })
    setTheArray(array);
    setReady(true);
  }

  async function getUsers() {
    let uri = 'http://localhost:3000/users';
    const sessionUser = localStorage.username;
    const res = await fetch(uri);
    const users = await res.json();
    
    users.map(user =>  {
      if (user.username === sessionUser) {
         console.log("winsss")
    }
      else {
        return;
      }

  })
}
  return (
    ready && (

<Container>
  <Row>
  {theArray.map((device, index) => ( 
  <Col>
    {device.id === 1 ? <img key={device.id} src={img1}></img> : null}
    {device.id === 2 ? <img key={index.toString()} src={img2}></img> : null}
    {device.id === 3 ? <img key={index.toString()} src={img3}></img> : null}
    <div>
    <h2 id = "title"  key={4}>
    {device.title}
    </h2>
    <p id = "description"key={5} >
    {device.description}
    </p>
    <p id = "model" key={6}>
    {device.model}
    </p>
    <p id = "version" key={7}>
    {device.version}
    </p>
    <p id = "enabled" key={8}>
    {device.enabled ? 'enabled' : 'not enabled'}
    </p>
    <p id = "connected" key={9}>
    {device.connected ? 'connected' : 'not connected'}
    </p>
    <p id = "timezone" key={10}>
    {device.timezone}
    </p>
    <div id = "storages" key={10}>
      {device.storages.map(storage => (
        storage.id ? <p> {storage.id} {storage.state}</p> : null))}      
    </div>
    </div>
  </Col>
)) }

  </Row>
  </Container>       
          

  ));
}
