import React, { useState, useEffect } from 'react';
import { Link, useParams } from "react-router";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import img1 from "../Images/axis-M2025.jpg";
import img2 from "../Images/axis-p1428.jpg";
import img3 from "../Images/axis-Q6000.jpg";

export default function Site1 () {
  const [ready, setReady] = useState(false);
  const [theArray, setTheArray] = useState();
  const {id} = useParams();
  useEffect(() => {
    // get all the devices from db when site render
    getDevices();
  }, []);
  async function getDevices() {
    let uri = 'http://localhost:3000/devices';
    let thisSiteId = id;
    const res = await fetch(uri);
    const posts = await res.json();
    let array = []
    console.log(thisSiteId)
    posts.map(device =>  {
    if (device.site_id == thisSiteId) {
       array.push(device)
    }     
    })
    setTheArray(array);
    setReady(true);
  }
  return (
    ready && (

<Container>
  <Row>
  {theArray.map((device, index) => ( 
  <Col>
    {device.id === 1 ? <img key={1} src={img1}></img> : null}
    {device.id === 2 ? <img key={2} src={img2}></img> : null}
    {device.id === 3 ? <img key={3} src={img3}></img> : null}
    <h2 id = "title"  key={index}>
    {device.title}
    </h2>
    <p id = "description" >
    {device.description}
    </p>
    <p id = "model">
    {device.model}
    </p>
    <p id = "version">
    {device.version}
    </p>
    <p id = "enabled">
    {device.enabled ? 'enabled' : 'not enabled'}
    </p>
    <p id = "connected">
    {device.connected ? 'connected' : 'not connected'}
    </p>
    <p id = "timezone">
    {device.timezone}
    </p>
  </Col>
)) }

  </Row>
  </Container>       
          

  ));
}
